from abc import ABC, abstractmethod

class VeiculoMotorizado(ABC):
    @abstractmethod
    def ligar_motor(self):
        pass

    @abstractmethod
    def desligar_motor(self):
        pass

class Carro(VeiculoMotorizado):
    def ligar_motor(self):
        print("Motor do carro ligado.")

    def desligar_motor(self):
        print("Motor do carro desligado.")

class Moto(VeiculoMotorizado):
    def ligar_motor(self):
        print("Motor da moto ligado.")

    def desligar_motor(self):
        print("Motor da moto desligado.")

meu_carro = Carro()
minha_moto = Moto()

meu_carro.ligar_motor()
minha_moto.ligar_motor()
meu_carro.desligar_motor()
minha_moto.desligar_motor()
